import React, {useState} from 'react';
import './Accordion.css';

const dataCollection = [
    {
        name: 'Lorem',
        recipes: 'Lorem'
    },
    {
        name: 'Lorem',
        recipes: 'Lorem'
    },
    {
        name: 'Lorem',
        recipes: 'Lorem'
    }
]

function Accordion() {
        const [accordion, setActiveAccordion] = useState(-1);
        function toggleAccordion(index) {
            setActiveAccordion(index);
        }
    return(
        <>
        <div className="container">
            <div>
                <span className="accordion__title"></span>
                <h1>Recipes sourced by you.</h1>
            </div>
            <div className="accordion__recipes">
                {dataCollection.map((item, index) =>
                    <div key={index} onClick={()=> toggleAccordion(index)}>
                        <div className="accordion__title-heading">
                            <h3 className={accordion === index ? "active" : ""}>{item.name}</h3>
                        </div>
                        <div>
                            {accordion === index ? (
                                <>
                                    <span className="verticle">-</span>
                                </>
                            ) : (
                                <>
                                    <span className="verticle">+</span>
                                </>
                            )}
                        </div>
                        <div>
                            <p className={accordion === index ? "active" : ""}>{item.recipes}</p>
                        </div>
                    </div>
                )}
            </div>
        </div>
        </>
    )
}


export default Accordion;
